(1, 'WebLoginSidebar', 'WebLogin Tpl', 0, 'none', 1, 0, '<!-- #declare:separator <hr> -->\n<!-- login form section-->\n<form method=\"post\" name=\"loginfrm\" action=\"[+action+]\">\n    <input type=\"hidden\" value=\"[+rememberme+]\" name=\"rememberme\" />\n    <fieldset>\n        <h3>Your Login Details</h3>\n        <label for=\"username\">User: <input type=\"text\" name=\"username\" id=\"username\" tabindex=\"1\" onkeypress=\"return webLoginEnter(document.loginfrm.password);\" value=\"[+username+]\" /></label>\n    	<label for=\"password\">Password: <input type=\"password\" name=\"password\" id=\"password\" tabindex=\"2\" onkeypress=\"return webLoginEnter(document.loginfrm.cmdweblogin);\" value=\"\" /></label>\n    	<input type=\"checkbox\" id=\"checkbox_1\" name=\"checkbox_1\" tabindex=\"3\" size=\"1\" value=\"\" [+checkbox+] onclick=\"webLoginCheckRemember()\" /><label for=\"checkbox_1\" class=\"checkbox\">Remember me</label>\n    	<input type=\"submit\" value=\"[+logintext+]\" name=\"cmdweblogin\" class=\"button\" />\n	<a href=\"#\" onclick=\"webLoginShowForm(2);return false;\" id=\"forgotpsswd\">Forget Your Password?</a>\n	</fieldset>\n</form>\n<hr>\n<!-- log out hyperlink section -->\n<h4>You\'re already logged in</h4>\nDo you wish to <a href=\"[+action+]\" class=\"button\">[+logouttext+]</a>?\n<hr>\n<!-- Password reminder form section -->\n<form name=\"loginreminder\" method=\"post\" action=\"[+action+]\">\n    <fieldset>\n        <h3>It happens to everyone...</h3>\n        <input type=\"hidden\" name=\"txtpwdrem\" value=\"0\" />\n        <label for=\"txtwebemail\">Enter the email address of your account to reset your password: <input type=\"text\" name=\"txtwebemail\" id=\"txtwebemail\" size=\"24\" /></label>\n        <label>To return to the login form, press the cancel button.</label>\n    	<input type=\"submit\" value=\"Submit\" name=\"cmdweblogin\" class=\"button\" /> <input type=\"reset\" value=\"Cancel\" name=\"cmdcancel\" onclick=\"webLoginShowForm(1);\" class=\"button\" style=\"clear:none;display:inline\" />\n    </fieldset>\n</form>\n\n', 0, 0, 0, 0),
(2, 'mm_demo_rules', 'ManagerManager rules for the demo content. Should be modified for your own sites.', 0, 'none', 2, 0, '// PHP *is* allowed\n// $news_role and $news_tpl will not apply to demo content but are left as a demonstration of what can be done\n\n// For everyone\nmm_default(\'pub_date\');\nmm_renameField(\'introtext\',\'Summary\');\nmm_changeFieldHelp(\'alias\', \'The URL that will be used to reach this resource. Only numbers, letters and hyphens can be used\');\nmm_widget_tags(\'documentTags\',\' \'); // Give blog tag editing capabilities to the \'documentTags (3)\' TV\nmm_widget_showimagetvs(); // Always give a preview of Image TVs\n// mm_widget_colors(\'color\', \'#666666\'); // make a color selector widget for the \'colour\' TV\n\n// For everyone except administrators\nmm_hideFields(\'link_attributes\', \'!1\');\nmm_hideFields(\'loginName \', \'!1\');\n// mm_renameField(\'alias\',\'URL alias\',\'!1\');\n\n// News editors role -- creating a variable makes it easier to manage if this changes in the future\n$news_role = \'3\';\nmm_hideFields(\'pagetitle,menutitle,link_attributes,template,menuindex,description,show_in_menu,which_editor,is_folder,is_richtext,log,searchable,cacheable,clear_cache\', $news_role);\nmm_renameTab(\'settings\', \'Publication settings\', $news_role);	\nmm_synch_fields(\'pagetitle,menutitle,longtitle\', $news_role);\nmm_renameField(\'longtitle\',\'Headline\', $news_role, \'\', \'This will be displayed at the top of each page\');\n\n// News story template\n$news_tpl = \'8\';\n// mm_createTab(\'Categories\',\'HrCats\', \'\', $news_tpl, \'\', \'600\');\n// mm_moveFieldsToTab(\'updateImage1\', \'general\', \'\', $news_tpl);\n// mm_hideFields(\'menuindex,show_in_menu\', \'\', $news_tpl);\nmm_changeFieldHelp(\'longtitle\', \'The story\\\'s headline\', \'\', $news_tpl);\nmm_changeFieldHelp(\'introtext\', \'A short summary of the story\', \'\', $news_tpl);\nmm_changeFieldHelp(\'parent\', \'To move this story to a different folder: Click this icon to activate, then choose a new folder in the tree on the left.\', \'\', $news_tpl);\n\n', 0, 0, 0, 0),
